package org.brainteam.lunchbox.cmd;import org.brainteam.lunchbox.i18n.Translator;import org.brainteam.lunchbox.jmx.ImportMenuConfiguration;import org.brainteam.lunchbox.services.OfferService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Component;@Component("importmenu")@Scope("prototype")public class ImportMenuCommand implements Command {		static final String NAME = "action.importmenu.name";		@Autowired	private OfferService offerService;		@Autowired	private Translator translator;		@Autowired	private ImportMenuConfiguration configuration;		@Override	public String getName() {		return getTranslator().t(NAME);	}	@Override	public void execute() throws CommandException {		try {			getOfferService().importURL(getConfiguration().getURL());		} catch (RuntimeException e) {			throw new CommandException(e);		}	}		protected OfferService getOfferService() {		return offerService;	}		protected Translator getTranslator() {		return translator;	}		protected ImportMenuConfiguration getConfiguration() {		return configuration;	}	}