package org.brainteam.lunchbox.cmd;import org.brainteam.lunchbox.i18n.Translator;import org.brainteam.lunchbox.services.OfferService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Component;@Component("lockoffers")@Scope("prototype")public class LockOffersCommand implements Command {		static final String NAME = "action.lockoffers.name";	@Autowired	private OfferService offerService;		@Autowired	private Translator translator;		@Override	public String getName() {		return getTranslator().t(NAME);	}		@Override	public void execute() throws CommandException {		try {			getOfferService().lockNext();		} catch (RuntimeException e) {			throw new CommandException(e);		}	}		protected OfferService getOfferService() {		return offerService;	}		protected Translator getTranslator() {		return translator;	}	}