package org.brainteam.lunchbox.core;import org.springframework.beans.BeansException;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.stereotype.Component;@Componentpublic class AppContext implements ApplicationContextAware {	private static ApplicationContext applicationContext;		@Override	public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {		AppContext.applicationContext = applicationContext;	}		public static <T> T getBean(Class<T> clazz) {		ApplicationContext ctx = get();		if (ctx == null) {			throw new LunchboxRuntimeException("applicationContext not set");		}		return get().getBean(clazz);	}		protected static ApplicationContext get() {		return applicationContext;	}	}