package org.brainteam.lunchbox.dao;import java.util.Date;import org.brainteam.lunchbox.domain.Journal;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;public interface JournalRepository extends JpaRepository<Journal, Long> {	@Query("SELECT j from Journal j " +			"WHERE j.modificationDate>=:from AND j.modificationDate<=:till")	Page<Journal> findByFromAndTillOrderByModificationDate(@Param("from") Date from, @Param("till") Date till, Pageable pageable);	}