package org.brainteam.lunchbox.dao;import org.brainteam.lunchbox.domain.Meal;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;public interface MealRepository extends JpaRepository<Meal, Long> {	Page<Meal> findByHeadlineLikeOrDescriptionLike(String headline, String description, Pageable page);		Meal findByHeadlineAndDescriptionAndIngredients(String headline, String description, String ingredients);	}