package org.brainteam.lunchbox.dao;import java.util.Date;import java.util.List;import org.brainteam.lunchbox.domain.Offer;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;public interface OfferRepository extends JpaRepository<Offer, Long> {		@Query("SELECT o FROM Offer o " +			"WHERE o.date >=:date " + 			"ORDER BY o.date")	List<Offer> findNextOrderByDateAsc(@Param("date") Date date);		@Query("SELECT o FROM Offer o " +			"WHERE o.date <=:date " + 			"AND o.locked=false " +			"ORDER BY o.date DESC")	List<Offer> findPreviousUnlockedOrderByDateDesc(@Param("date") Date date);	@Query("SELECT o FROM Offer o " +			"WHERE o.date>=:from AND o.date<=:till " +			"ORDER BY o.date")	List<Offer> findByFromAndTillOrderByDate(@Param("from") Date from, @Param("till") Date till);	}