package org.brainteam.lunchbox.dao;import java.util.List;import org.brainteam.lunchbox.domain.User;import org.springframework.data.jpa.repository.JpaRepository;public interface UserRepository extends JpaRepository<User, Long> {	List<User> findByActiveTrue();		List<User> findByAdminRoleTrue();		User findByLoginNameAndLoginSecret(String loginName, String loginSecret);		User findByActiveTrueAndSuperUserTrue();	}