package org.brainteam.lunchbox.json;public class JsonMeal {	private Long id;	private String headline;	private String description;	private String ingredients;	private Long offerItemsCount;		public Long getId() {		return id;	}		public void setId(Long id) {		this.id = id;	}		public String getHeadline() {		return headline;	}		public void setHeadline(String headline) {		this.headline = headline;	}		public String getDescription() {		return description;	}		public void setDescription(String description) {		this.description = description;	}		public String getIngredients() {		return ingredients;	}		public void setIngredients(String ingredients) {		this.ingredients = ingredients;	}		public Long getOfferItemsCount() {		return offerItemsCount;	}		public void setOfferItemsCount(Long offerItemsCount) {		this.offerItemsCount = offerItemsCount;	}	}