package org.brainteam.lunchbox.json;import java.util.ArrayList;import java.util.Date;import java.util.List;public class JsonOrders {	private Date date;	private JsonOffer offer;	private List<JsonOrder> orders;		public Date getDate() {		return date;	}		public void setDate(Date date) {		this.date = date;	}		public JsonOffer getOffer() {		return offer;	}		public void setOffer(JsonOffer offer) {		this.offer = offer;	}		public List<JsonOrder> getOrders() {		if (orders == null) {			return new ArrayList<>();		}		return orders;	}		public void addToOrders(JsonOrder order) {		if (orders == null) {			orders = new ArrayList<>();		}		orders.add(order);	}	}