package org.brainteam.lunchbox.json;import java.util.ArrayList;import java.util.List;public class JsonPage<T extends Object> {	private int totalPages;	private long totalItems;	private int numberOfItems;	private int page;	private int size;	private String sort;	private String order;	private String search;	private List<T> items;		public long getTotalItems() {		return totalItems;	}		public void setTotalItems(long totalItems) {		this.totalItems = totalItems;	}		public int getTotalPages() {		return totalPages;	}		public void setTotalPages(int totalPages) {		this.totalPages = totalPages;	}		public int getPage() {		return page;	}	public void setPage(int page) {		this.page = page;	}		public int getNumberOfItems() {		return numberOfItems;	}		public void setNumberOfItems(int size) {		this.numberOfItems = size;	}		public int getSize() {		return size;	}		public void setSize(int size) {		this.size = size;	}		public String getSort() {		return sort;	}		public void setSort(String sort) {		this.sort = sort;	}		public String getOrder() {		return order;	}		public void setOrder(String order) {		this.order = order;	}		public String getSearch() {		return search;	}		public void setSearch(String search) {		this.search = search;	}		public List<T> getItems() {		if (items == null) {			return new ArrayList<>();		}		return items;	}		public void addToItems(T o) {		if (items == null) {			items = new ArrayList<>();		}		items.add(o);	}	}