package org.brainteam.lunchbox.json;public class JsonPlugin {	private String name;	private String author;	private String description;	private String version;		public String getName() {		return name;	}		public void setName(String name) {		this.name = name;	}		public String getAuthor() {		return author;	}		public void setAuthor(String author) {		this.author = author;	}		public String getDescription() {		return description;	}		public void setDescription(String description) {		this.description = description;	}		public String getVersion() {		return version;	}		public void setVersion(String version) {		this.version = version;	}	}