package org.brainteam.lunchbox.json;import java.util.ArrayList;import java.util.List;public class JsonSystemStatistics {	private List<JsonStatisticsNamedValueItem> namedValues;	private List<JsonStatisticsNamedValueItem> greediestUsers;	private List<JsonStatisticsNamedValueItem> popularMeals;	private Object[] salesByMonth;	private List<JsonTopUser> topUsers;	private JsonPieChartData[] menuDistribution;		public List<JsonStatisticsNamedValueItem> getNamedValues() {		return namedValues;	}		public void addToNamedValues(JsonStatisticsNamedValueItem item) {		if (namedValues == null) {			namedValues = new ArrayList<>();		}		namedValues.add(item);	}		public List<JsonStatisticsNamedValueItem> getGreediestUsers() {		return greediestUsers;	}		public void setGreediestUsers(List<JsonStatisticsNamedValueItem> greediestUsers) {		this.greediestUsers = greediestUsers;	}		public List<JsonStatisticsNamedValueItem> getPopularMeals() {		return popularMeals;	}		public void setPopularMeals(List<JsonStatisticsNamedValueItem> popularMeals) {		this.popularMeals = popularMeals;	}		public Object[] getSalesByMonth() {		return salesByMonth;	}		public void setSalesByMonth(Object[] salesByMonth) {		this.salesByMonth = salesByMonth;	}		public List<JsonTopUser> getTopUsers() {		if (topUsers == null) {			topUsers = new ArrayList<>();		}		return topUsers;	}		public void addToTopUsers(JsonTopUser item) {		if (topUsers == null) {			topUsers = new ArrayList<>();		}		topUsers.add(item);	}		public JsonPieChartData[] getMenuDistribution() {		return menuDistribution;	}		public void setMenuDistribution(JsonPieChartData[] menuDistribution) {		this.menuDistribution = menuDistribution;	}	}