package org.brainteam.lunchbox.json;import java.util.ArrayList;import java.util.List;public class JsonUserStatistics {	private Object[] salesByMonth;	private List<JsonStatisticsNamedValueItem> namedValues;	private List<JsonStatisticsNamedValueItem> popularMeals;	private Object[] menuDistribution;		public Object[] getSalesByMonth() {		return salesByMonth;	}		public void setSalesByMonth(Object[] salesByMonth) {		this.salesByMonth = salesByMonth;	}		public List<JsonStatisticsNamedValueItem> getNamedValues() {		return namedValues;	}		public void addToNamedValues(JsonStatisticsNamedValueItem item) {		if (namedValues == null) {			namedValues = new ArrayList<>();		}		namedValues.add(item);	}		public List<JsonStatisticsNamedValueItem> getPopularMeals() {		return popularMeals;	}		public void setPopularMeals(List<JsonStatisticsNamedValueItem> popularMeals) {		this.popularMeals = popularMeals;	}		public Object[] getMenuDistribution() {		return menuDistribution;	}		public void setMenuDistribution(Object[] menuDistribution) {		this.menuDistribution = menuDistribution;	}	}