package org.brainteam.lunchbox.out;import java.util.List;public class CollectedMailException extends MailException {	private static final long serialVersionUID = 4611781631427210679L;	private List<MailException> causes;		public CollectedMailException(List<MailException> causes) {		super(causes.get(0));		this.causes = causes;	}		public List<MailException> getCauses() {		return causes;	}	}