package org.brainteam.lunchbox.out;import java.io.File;public class MailAttachment {	private String name;	private String description;	private File file;		public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}		public String getDescription() {		return description;	}		public void setDescription(String description) {		this.description = description;	}		public File getFile() {		return file;	}		public void setFile(File file) {		this.file = file;	}	}