package org.brainteam.lunchbox.out;import java.util.ArrayList;import java.util.List;public class MailData {	private String subject;	private String message;	private List<MailAttachment> attachments;		public String getSubject() {		return subject;	}		public void setSubject(String subject) {		this.subject = subject;	}		public String getMessage() {		return message;	}		public void setMessage(String message) {		this.message = message;	}		public List<MailAttachment> getAttachments() {		return attachments;	}		public void addToAttachments(MailAttachment attachment) {		if (attachments == null) {			attachments = new ArrayList<>();		}		attachments.add(attachment);	}	}