package org.brainteam.lunchbox.templates;import java.util.Map;import javax.annotation.PostConstruct;import org.brainteam.lunchbox.jmx.MailBillingTextTemplateConfiguration;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Componentpublic class MailBillingTextTemplateImpl extends VelocityTextTemplate implements MailBillingTextTemplate {		@Autowired	private MailBillingTextTemplateConfiguration configuration;		@PostConstruct	protected void configure() {		setFileName(getConfiguration().getFileName());	}		@Override	protected void checkParams(Map<String,Object> params) throws TextTemplateException {		checkParam(params, CREATION_DATE);		checkParam(params, BILLING_INFO);		checkParam(params, NAME_OF_MONTH);		checkParam(params, YEAR);	}		protected MailBillingTextTemplateConfiguration getConfiguration() {		return configuration;	}}