package org.brainteam.lunchbox.templates;import java.util.Map;import javax.annotation.PostConstruct;import org.brainteam.lunchbox.jmx.MailOrderTextTemplateConfiguration;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Componentpublic class MailOrderTextTemplateImpl extends VelocityTextTemplate implements MailOrderTextTemplate {		@Autowired	private MailOrderTextTemplateConfiguration configuration;		@PostConstruct	protected void configure() {		setFileName(getConfiguration().getFileName());	}		@Override	protected void checkParams(Map<String,Object> params) throws TextTemplateException {		checkParam(params, CREATION_DATE);		checkParam(params, ORDER_DATE);		checkParam(params, ORDER_INFO);	}		protected MailOrderTextTemplateConfiguration getConfiguration() {		return configuration;	}}