package org.brainteam.lunchbox.tools;import org.brainteam.lunchbox.cmd.BackupDatabaseAction;import org.brainteam.lunchbox.cmd.CommandException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.jmx.export.annotation.ManagedOperation;import org.springframework.jmx.export.annotation.ManagedResource;import org.springframework.stereotype.Component;@Component@ManagedResource(objectName = "org.brainteam.lunchbox.tools:name=Datenbank-Backup", description = "Online-Backup der Datenbank in das Dateisystem")public class DatabaseBackup {		@Autowired	private BackupDatabaseAction action;		@ManagedOperation	public void doBackup() {		try {			getAction().execute();		} catch (CommandException e) {			throw new RuntimeException(e);		}	}		public BackupDatabaseAction getAction() {		return action;	}	}