package org.brainteam.lunchbox.util;import org.eclipse.persistence.sessions.Session;import org.eclipse.persistence.config.SessionCustomizer;public class DBSchemaIntegrityChecker implements SessionCustomizer {	@Override	public void customize(Session session) throws Exception {		session.getIntegrityChecker().checkDatabase();			session.getIntegrityChecker().setShouldCatchExceptions(false);	}}