package org.brainteam.lunchbox.core;import static org.mockito.Mockito.verify;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.springframework.context.ApplicationContext;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;@Test(groups="unit")public class AppContextTest {	@Mock	private ApplicationContext applicationContext;		@InjectMocks	private AppContext appContext = new AppContext();		@BeforeClass	public void setup() {		MockitoAnnotations.initMocks(this);	}		@Test(expectedExceptions=LunchboxRuntimeException.class)	public void testGetBeanUnitialized() {		AppContext.getBean(AppContextTest.class);	}		@Test(dependsOnMethods="testGetBeanUnitialized")	public void testGetBean() {		appContext.setApplicationContext(applicationContext);		Class<?> clazz = AppContextTest.class;		AppContext.getBean(clazz);		verify(applicationContext).getBean(clazz);	}	}