package org.brainteam.lunchbox.core;import static org.testng.Assert.assertFalse;import static org.testng.Assert.assertNotNull;import java.util.Date;import org.testng.annotations.Test;@Test(groups="unit")public class ClockImplTest {	private ClockImpl clock = new ClockImpl();		@Test	public void test() throws InterruptedException {		Date now = clock.now();		assertNotNull(now);		Thread.sleep(1);		assertFalse(now.equals(clock.now()));	}	}