package org.brainteam.lunchbox.core;import static org.testng.Assert.assertTrue;import static org.testng.Assert.assertNotNull;import java.io.File;import org.apache.commons.lang.StringUtils;import org.testng.annotations.Test;@Test(groups="unit")public class DirectoriesImplTest {	private DirectoriesImpl directories = new DirectoriesImpl();		@Test	public void testGetBackupDir() {		File dir = directories.getBackupDir();		assertTrue(StringUtils.startsWith(dir.getAbsolutePath(), directories.getBackupBaseDir().getAbsolutePath()));		assertNotNull(dir);	}		@Test	public void testGetTempDir() {		File dir = directories.getTempDir();		assertNotNull(dir);		assertTrue(StringUtils.startsWith(dir.getAbsolutePath(), DirectoriesImpl.getBaseDir().getAbsolutePath()));		assertTrue(StringUtils.endsWith(dir.getAbsolutePath(), File.separator + "temp"));	}		@Test	public void testGetConfigDir() {		File dir = directories.getConfigDir();		assertNotNull(dir);		assertTrue(StringUtils.startsWith(dir.getAbsolutePath(), DirectoriesImpl.getBaseDir().getAbsolutePath()));		assertTrue(StringUtils.endsWith(dir.getAbsolutePath(), File.separator + "config"));	}		@Test	public void testGetTemplatesDir() {		File dir = directories.getTemplatesDir();		assertNotNull(dir);		assertTrue(StringUtils.startsWith(dir.getAbsolutePath(), directories.getConfigDir().getAbsolutePath()));		assertTrue(StringUtils.endsWith(dir.getAbsolutePath(), File.separator + "templates"));	}		@Test	public void testGetDataDir() {		File dir = directories.getDataDir();		assertNotNull(dir);		assertTrue(StringUtils.startsWith(dir.getAbsolutePath(), DirectoriesImpl.getBaseDir().getAbsolutePath()));		assertTrue(StringUtils.endsWith(dir.getAbsolutePath(), File.separator + "data"));	}		@Test	public void testGetPluginDir() {		File dir = directories.getPluginDir();		assertNotNull(dir);		assertTrue(StringUtils.startsWith(dir.getAbsolutePath(), DirectoriesImpl.getBaseDir().getAbsolutePath()));		assertTrue(StringUtils.endsWith(dir.getAbsolutePath(), File.separator + "plugins"));	}	}