package org.brainteam.lunchbox.domain;import static org.testng.Assert.assertSame;import java.util.Arrays;import java.util.Collections;import java.util.List;import org.testng.annotations.Test;@Test(groups="unit")public class OfferItemTest {	@Test	public void testComparable() {		OfferItem oi1 = new OfferItem();		oi1.setName("Menü 3");				OfferItem oi2 = new OfferItem();		oi2.setName("Menü 2");				OfferItem oi3 = new OfferItem();		oi3.setName("Menü 1");				List<OfferItem> ois = Arrays.asList(oi1, oi2, oi3);		Collections.sort(ois);				assertSame(ois.get(0), oi3);		assertSame(ois.get(1), oi2);		assertSame(ois.get(2), oi1);	}	}