package org.brainteam.lunchbox.domain;import static org.testng.Assert.assertSame;import java.util.Arrays;import java.util.Collections;import java.util.List;import org.testng.annotations.Test;@Test(groups="unit")public class OrderTest {	@Test	public void testComparable() {		OfferItem oi1 = new OfferItem();		oi1.setName("Menü 3");		Order o1 = new Order();		o1.setItem(oi1);				OfferItem oi2 = new OfferItem();		oi2.setName("Menü 2");		Order o2 = new Order();		o2.setItem(oi2);				OfferItem oi3 = new OfferItem();		oi3.setName("Menü 1");		Order o3 = new Order();		o3.setItem(oi3);				List<Order> orders = Arrays.asList(o1, o2, o3);		Collections.sort(orders);				assertSame(orders.get(0), o3);		assertSame(orders.get(1), o2);		assertSame(orders.get(2), o1);	}	}