package org.brainteam.lunchbox.in;import static org.mockito.Mockito.when;import static org.testng.Assert.assertNotNull;import static org.testng.Assert.assertTrue;import java.io.File;import java.io.IOException;import org.apache.commons.io.FileUtils;import org.brainteam.lunchbox.core.Directories;import org.brainteam.lunchbox.jmx.ImportMenuConfigurationImpl;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;@Test(groups={"unit"})public class DownloaderImplTest {	@Mock	private Directories directories;		@InjectMocks	private DownloaderImpl downloader = new DownloaderImpl();		@BeforeClass	public void setup() {		MockitoAnnotations.initMocks(this);		when(directories.getTempDir()).thenReturn(FileUtils.getTempDirectory());	}		@Test	public void test() throws IOException {		File downloadedFile = downloader.download(ImportMenuConfigurationImpl.URL_DEFAULT);		assertNotNull(downloadedFile);		assertTrue(downloadedFile.length() > 0);	}	}