package org.brainteam.lunchbox.jmx;import org.mockito.InjectMocks;import org.testng.annotations.Test;@Test(groups= { "jmx", "unit", "broken" })public class DailyOrderPdfReportConfigurationImplTest extends BaseConfigurationTest {		private static final String INFO = "info";	@InjectMocks	private final DailyOrderPdfReportConfigurationImpl config = new DailyOrderPdfReportConfigurationImpl();	@Override	protected String getFilename() {		return DailyOrderPdfReportConfigurationImpl.CONFIGURATION_FILENAME;	}		@Test	public void testGetAndSetInfo() {		testGetAndSet(new TestCase() {			@Override			protected String getPropertyName() {				return DailyOrderPdfReportConfigurationImpl.INFO_PROPERTY;			}			@Override			protected Object getDefaultValue() {				return DailyOrderPdfReportConfigurationImpl.INFO_DEFAULT;			}			@Override			protected Object getUpdateValue() {				return INFO;			}			@Override			protected Object getCurrentValue() {				return config.getInfo();			}			@Override			protected void setValue(Object value) {				config.setInfo((String)value);			}					});	}	}