package org.brainteam.lunchbox.jmx;import static org.testng.Assert.assertEquals;import org.mockito.InjectMocks;import org.testng.annotations.Test;@Test(groups= { "jmx", "unit", "broken" })public class ImportMenuConfigurationImplTest extends BaseConfigurationTest {		private static final String URL = "http://www.test.de";	@InjectMocks	private final ImportMenuConfigurationImpl config = new ImportMenuConfigurationImpl();		@Override	protected String getFilename() {		return ImportMenuConfigurationImpl.CONFIGURATION_FILENAME;	}		@Test	public void testGetAndSetUrl() {		testGetAndSet(new TestCase() {			@Override			protected String getPropertyName() {				return ImportMenuConfigurationImpl.URL_PROPERTY;			}			@Override			protected Object getDefaultValue() {				return ImportMenuConfigurationImpl.URLSPEC_DEFAULT;			}			@Override			protected Object getUpdateValue() {				return URL;			}			@Override			protected Object getCurrentValue() {				return config.getUrlValue();			}			@Override			protected void setValue(Object value) {				config.setUrlValue((String)value);			}						@Override			void checkValue(Object expectedValue) {				super.checkValue(expectedValue);				assertEquals(config.getURL().toExternalForm(), (String)expectedValue);			}					});	}}