package org.brainteam.lunchbox.mvc;import org.brainteam.lunchbox.json.JsonMeal;import org.brainteam.lunchbox.services.MealService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseStatus;@Controller@RequestMapping("/json/meal")public class MealResource {		@Autowired	private MealService mealService;	@RequestMapping(method = RequestMethod.PUT)	@ResponseStatus(HttpStatus.OK)	public void put(@RequestBody JsonMeal json) {		getMealService().createNew(json);	}	@RequestMapping(value = "{id}", method = RequestMethod.POST)	@ResponseStatus(HttpStatus.OK)	public void post(@PathVariable Long id, @RequestBody JsonMeal json) {		json.setId(id);		getMealService().updateExisting(json);	}		@RequestMapping(value = "{id}", method = RequestMethod.DELETE)	@ResponseStatus(HttpStatus.OK)	public void delete(@PathVariable Long id) {		getMealService().deleteExisting(id);	}		protected MealService getMealService() {		return mealService;	}}