package org.brainteam.lunchbox.mvc;import org.brainteam.lunchbox.json.JsonMeal;import org.brainteam.lunchbox.json.JsonPage;import org.brainteam.lunchbox.services.MealService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;@Controller@RequestMapping("/json/meals")public class MealsResource {		@Autowired	private MealService mealService;		@RequestMapping(method=RequestMethod.GET)	public @ResponseBody JsonPage<JsonMeal> searchMeals(@RequestParam int page, @RequestParam int size, @RequestParam String sort,			@RequestParam String order, @RequestParam(required=false) String search) {		return getMealService().getJson(page - 1, size, sort, order, search);	}		protected MealService getMealService() {		return mealService;	}	}