package org.brainteam.lunchbox.mvc;import java.util.Date;import java.util.List;import org.brainteam.lunchbox.json.JsonOption;import org.brainteam.lunchbox.services.OptionService;import org.brainteam.lunchbox.util.DateUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Controller@RequestMapping("/json/options")public class OptionResource {		@Autowired	private OptionService optionService;		@RequestMapping(value="/meals", method=RequestMethod.GET)	public @ResponseBody List<JsonOption> getMeals() {		return getOptionService().getMeals();	}		@RequestMapping(value="/offeritems/{year}/{month}/{day}", method=RequestMethod.GET)	public @ResponseBody List<JsonOption> getOffer(@PathVariable Integer year, @PathVariable Integer month, @PathVariable Integer day) {		Date date = DateUtils.toDate(year, month, day);		return getOptionService().getOfferItems(date);	}	@RequestMapping(value="/users", method=RequestMethod.GET)	public @ResponseBody List<JsonOption> getUsers() {		return getOptionService().getUsers();	}		protected OptionService getOptionService() {		return optionService;	}}