package org.brainteam.lunchbox.mvc;import java.util.Date;import org.brainteam.lunchbox.json.JsonOrders;import org.brainteam.lunchbox.services.OrderService;import org.brainteam.lunchbox.util.DateUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Controller@RequestMapping("/json/orders")public class OrdersResource {		@Autowired	private OrderService orderService;		@RequestMapping(value="{year}/{month}/{day}", method=RequestMethod.GET)	public @ResponseBody JsonOrders getForDay(@PathVariable Integer year, @PathVariable Integer month, @PathVariable Integer day) {				Date date = DateUtils.toDate(year, month, day);		return getOrderService().getJsonOrders(date);	}		protected OrderService getOrderService() {		return orderService;	}	}