package org.brainteam.lunchbox.util;import java.io.IOException;import java.io.InputStream;import java.util.jar.Attributes;import java.util.jar.Manifest;import javax.servlet.ServletContext;public final class WebUtils {		private static final String DEFAULT_VERSION = "0.0.2-SNAPSHOT";	private WebUtils() {	}		public static String getVersion(ServletContext servletContext) {		try (InputStream in = servletContext.getResourceAsStream("/META-INF/MANIFEST.MF")) {			if (in == null) {				return DEFAULT_VERSION;			}			Manifest manifest = new Manifest(in);			Attributes attribs = manifest.getMainAttributes();			String version = attribs.getValue("Implementation-Version");			return version != null ? version : DEFAULT_VERSION;		} catch (IOException e) {			return DEFAULT_VERSION;		}	}	}