package org.brainteam.lunchbox.mvc;import static org.mockito.Mockito.verify;import org.brainteam.lunchbox.json.JsonMeal;import org.brainteam.lunchbox.services.MealService;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;@Test(groups={"unit"})public class MealResourceTest {		private static final Long ID = Long.valueOf(1);		@Mock	private JsonMeal json;	@Mock	private MealService mealService;		@InjectMocks	private MealResource mealResource;		@BeforeClass	public void setup() {		MockitoAnnotations.initMocks(this);	}		@Test	public void testPut() {		mealResource.put(json);		verify(mealService).createNew(json);	}		@Test	public void testPost() {		mealResource.post(ID, json);		verify(mealService).updateExisting(json);	}		@Test	public void testDelete() {		mealResource.delete(ID);		verify(mealService).deleteExisting(ID);	}	}