package org.brainteam.lunchbox.mvc;import static org.mockito.Mockito.verify;import org.brainteam.lunchbox.json.JsonOfferItem;import org.brainteam.lunchbox.services.OfferItemService;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;@Test(groups={"unit"})public class OfferItemResourceTest {		private static final Long ID = Long.valueOf(1);		@Mock	private JsonOfferItem json;	@Mock	private OfferItemService offerItemService;		@InjectMocks	private OfferItemResource offerItemResource;		@BeforeClass	public void setup() {		MockitoAnnotations.initMocks(this);	}		@Test	public void testPut() {		offerItemResource.put(json);		verify(offerItemService).createNew(json);	}		@Test	public void testPost() {		offerItemResource.post(ID, json);		verify(offerItemService).updateExisting(json);	}		@Test	public void testDelete() {		offerItemResource.delete(ID);		verify(offerItemService).deleteExisting(ID);	}	}