package org.brainteam.lunchbox.mvc;import static org.mockito.Mockito.verify;import static org.mockito.Mockito.when;import static org.testng.Assert.assertSame;import org.brainteam.lunchbox.json.JsonOffers;import org.brainteam.lunchbox.services.OfferService;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;@Test(groups={"unit"})public class OffersResourceTest {	@Mock	private JsonOffers json;		@Mock	private OfferService offerService;		@InjectMocks	private OffersResource offersResource;		@BeforeClass	public void setup() {		MockitoAnnotations.initMocks(this);		when(offerService.getNextAsJson()).thenReturn(json);	}		@Test	public void testGet() {		JsonOffers result = offersResource.get();		verify(offerService).getNextAsJson();		assertSame(result, json);	}	}