package org.brainteam.lunchbox.mvc;import java.util.List;import static org.mockito.Mockito.verify;import static org.mockito.Mockito.when;import static org.testng.Assert.assertSame;import org.brainteam.lunchbox.json.JsonSchedulerJob;import org.brainteam.lunchbox.scheduler.BackgroundExecutor;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;@Test(groups={"unit"})public class SchedulerResourceTest {		@Mock	private List<JsonSchedulerJob> json;	@Mock	private BackgroundExecutor backgroundExecutor;		@InjectMocks	private SchedulerResource schedulerResource;		@BeforeClass	public void setup() {		MockitoAnnotations.initMocks(this);		when(backgroundExecutor.getJobInfo()).thenReturn(json);	}		@Test	public void testGetJobs() {		List<JsonSchedulerJob> result = schedulerResource.getJobs();		verify(backgroundExecutor).getJobInfo();		assertSame(result, json);	}	}